// นำเข้า React และ React Router สำหรับจัดการเส้นทาง (Routing)
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// นำเข้า Components ต่าง ๆ ที่ใช้ในแอปพลิเคชัน
import Login from "./components/Login";
import SignUp from "./components/SignUp";
import Dashboard from "./components/Dashboard";
import EditProfile from "./components/EditProfile";
import AddCourse from "./components/AddCourse";
import ManageClassroom from "./components/ManageClassroom";
import Checkin from "./components/Checkin";
import QnA from "./components/QnA";

function App() {
  return (
    // ใช้ Router เพื่อครอบเส้นทางทั้งหมด
    <Router>
      <Routes>
        {/* เส้นทางสำหรับหน้า Login (หน้าแรกของแอปพลิเคชัน) */}
        <Route path="/" element={<Login />} />

        
        <Route path="/signup" element={<SignUp />} />

        
        <Route path="/dashboard" element={<Dashboard />} />

        
        <Route path="/edit-profile" element={<EditProfile />} />

        
        <Route path="/add-course" element={<AddCourse />} />

        
        <Route path="/manage/:cid" element={<ManageClassroom />} />

        
        <Route path="/checkin/:cid/:cno" element={<Checkin />} />

        
        <Route path="/qna/:cid/:cno" element={<QnA />} />
      </Routes>
    </Router>
  );
}

// ส่งออก Component App เพื่อให้สามารถนำไปใช้ในไฟล์อื่น ๆ ได้
export default App;
